<?phpif (!defined('_CAN_LOAD_FILES_'))    define('_CAN_LOAD_FILES_', 1);class TDmanufacturerBlock extends Module {    public $entities = array();    private $_params = '';    function __construct() {        $this->name = 'tdmanufacturerblock';        $this->tab = 'front_office_features';        $this->version = 1.1;        $this->author = 'ThemesDeveloper';        $this->need_instance = 0;        parent::__construct();        $this->displayName = $this->l('ThemesDeveloper Manufacturers Block');        $this->description = $this->l('Manufacturers Block');    }    function install() {        if (!parent::install())            return false;        if (!$this->registerHook('home'))            return false;        if (!$this->registerHook('header'))            return false;        return true;    }    function hookHome($params) {        global $smarty, $cookie, $link;        $manufacturers = Manufacturer::getManufacturers(false, $cookie->id_lang, true);        $smarty->assign(                array(                    'manufacturers' => $manufacturers                )        );        return $this->display(__FILE__, 'tdmanufacturerblock.tpl');    }}